    $Date_darrivée = $trip["Date_darrivée"];
    $Date_de_sortie = $trip["Date_de_sortie"];
    $Description_brève_du_voyage = $trip["Description_brève_du_voyage"];
    $Description_du_voyage = $trip["Description_du_voyage"];
    $le_prix_comprend = $trip["le_prix_comprend"];
    $Le_prix_ne_comprend_pas = $trip["Le_prix_ne_comprend_pas"];
