    $Image1 = $trip["Image1"];
    $Image2 = $trip["Image2"];
    $Image3 = $trip["Image3"];

    // Prepare SQL statement with placeholders for values
    $sql = "INSERT INTO trip (titre,prix_avant,prix_apres,remise,Destination,Nombre_de_nuits,Date_darrivée,Date_de_sortie,
                  Description_brève_du_voyage,Description_du_voyage,le_prix_comprend,
