            VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";

    // Prepare statement and bind parameters
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("ssssssssssssssss", $titre, $prix_avant, $prix_apres, $remise,
        $Destination, $Nombre_de_nuits, $Date_darrivée, $Date_de_sortie, $Description_brève_du_voyage,
        $Description_du_voyage, $le_prix_comprend, $Le_prix_ne_comprend_pas,
        $Background_image, $Image1, $Image2, $Image3);

    // Execute statement
    if ($stmt->execute()) {
        // do nothing
    } else {
        echo "Error creating trip: " . $conn->error . "<br>";
    }
}

// Close prepared statement and database connection
$stmt->close();
$conn->close();

